import './main.css';
import React, { useRef, useState } from 'react';
import Header from './components/header/Header';

function App() {
  const [todoList, setTodoList] = useState([
    {
      title : "다이어트",
      content : "이번주 20kg 감량",
      isDone : false,
    },
    {
      title : "책읽기",
      content : "이번주 목표 3장 읽기",
      isDone : false,
    }
  ]);

  const titleRef = useRef();
  const contentRef = useRef();

  const addContents = () => {
    setTodoList([...todoList, {
          title : titleRef.current.value,
          content : contentRef.current.value,
          isDone : false,
        }
      ]
    )
    titleRef.current.value = "";
    contentRef.current.value = "";
  }

  const deleteTodo = (index) => {
    const filterArray = todoList.filter((todoItem, _index) => {
      return _index !== index})
    setTodoList(filterArray)
  }

  const doneTodo = (index) => {
    const doneList = todoList.map((todo, _index) => {
    if(index === _index) {
      return {...todo, isDone:true}
    } else return todo
  })
  setTodoList(doneList)
  }

  const notdoneTodo = (index) => {
    const doneList = todoList.map((todo, _index) => {
    if(index === _index) {
      return {...todo, isDone:false}
    } else return todo
  })
  setTodoList(doneList)
  }

  return (
    <div className="wrap">
      <Header
        titleRef={titleRef}
        contentRef={contentRef}
        addContents={addContents}
      />

      <div className="">
        <h1>WORKING</h1>

        {todoList.map((todoListName, index) => {
          return (
            <>
              {todoListName.isDone ? (
                ""
              ) : (
                <div className="box_wrap" key={index}>
                  <div className="todolistbox">
                    <span className="title">{todoListName.title}</span>
                    <span className="main">{todoListName.content}</span>

                    <div className="mybtns">
                      <button
                        onClick={() => {
                          deleteTodo(index);
                        }}
                      >
                        삭제
                      </button>
                      <button
                        onClick={() => {
                          doneTodo(index);
                        }}
                      >
                        완료
                      </button>
                    </div>
                  </div>
                </div>
              )}
            </>
          );
        })}

        <h1>DONE</h1>

        {todoList.map((todoListName, index) => {
          return (
            <>
              {todoListName.isDone ? (
                <div className="box_wrap" key={index}>
                  <div className="todolistbox">
                    <span className="title">{todoListName.title}</span>
                    <span className="main">{todoListName.content}</span>

                    <div className="mybtns">
                      <button
                        onClick={() => {
                          deleteTodo(index);
                        }}
                      >
                        삭제
                      </button>
                      <button
                        onClick={() => {
                          notdoneTodo(index);
                        }}
                      >
                        취소
                      </button>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </>
          );
        })}
      </div>
    </div>
  );
}

export default App;
