import './main.css';
import React, { useState } from 'react';

function App() {
  const [title, setTitle] = useState();
  const [content, setContents] = useState();
  const toDo = [];

  function onChangeHandelr(event) {
    const inputTitle = event.target.value;

    setTitle(inputTitle)
  }

  function onChangeHandelr2(event) {
    const inputContents = event.target.value;

    setContents(inputContents)
  }

  function onClickHandler() {
    toDo.push(title)
    toDo.push(content)
  }



  return (
    <div className="wrap">

      <div className="header">
        <span>My Todo List</span>
        <span>React</span>
      </div>

      <div className="under_header">

        <div className="under_header_1">
          제목<input type="text" value={title} onChange={onChangeHandelr}></input>
          내용<input type="text" value={content} onChange={onChangeHandelr2}></input>
        </div>

        <div className="under_header_2">
          <button onClick={onClickHandler}>
            추가하기
          </button>
        </div>

        <div className='notdone'>

        </div>

        <div className='done'>

        </div>

      </div>

    </div>
  );
}

export default App;
