import React, { useState } from "react";
import Todo from "../todo/Todo";
import "./style.css";

function List(props) {
  const deleteTodo = (index) => {
    const filterArray = props.todoList.filter((todoItem, _index) => {
      return _index !== index;
    });
    props.setTodoList(filterArray);
  };

  const togleTodo = (isDone, index, button) => {
    if(button === "완료") {
      return setButton('취소')
    } else if (button === "취소") {
      return setButton('완료')
    }

    const doneList = props.todoList.map((todo, _index) => {
      if (index === _index) {
        return isDone ? { ...todo, isDone: false } : { ...todo, isDone: true };
      } else return todo;
    });
    props.setTodoList(doneList);
  };

  const [button, setButton] = useState('완료')

  return (
    <div className="todoListBox">
      <h1> Working </h1>
      <div className="repeatForm">
        {props.todoList.map((todoItem, index) =>
          todoItem.isDone ? (
            ""
          ) : (
            <Todo
              todoItem={todoItem}
              togleTodo={togleTodo}
              deleteTodo={deleteTodo}
              index={index}
              button={button}
            />
          )
        )}
      </div>

      <h1> Done </h1>
      <div className="repeatForm">
        {props.todoList.map((todoItem, index) =>
          !todoItem.isDone ? (
            ""
          ) : (
            <Todo
              todoItem={todoItem}
              togleTodo={togleTodo}
              deleteTodo={deleteTodo}
              index={index}
              button={button}
            />
          )
        )}
      </div>
    </div>
  );
}

export default List;
