import './main.css';
import React, { useRef, useState } from 'react';

function App() {
  const [todoList, setTodoList] = useState([
    {
      title : "밥먹기",
      content : "냠냠",
      isDone : false,
    },
    {
      title : "책읽기",
      content : "랄랄",
      isDone : false,
    }
  ]);

  const [done, setDone] = useState([
    {
      title : "세수하기",
      content : "슥슥",
      isDone : true,
    },
    {
      title : "잠자기",
      content : "쿨쿨",
      isDone : true,
    }
  ]);

  const titleRef = useRef();
  const contentRef = useRef();

  const addContents = () => {
    setTodoList([...todoList, {
          title : titleRef.current.value,
          content : contentRef.current.value,
          isDone : false,
        }
      ]
    )
  }

  const deleteTodo = (index) => {
    const filterArray = todoList.filter((todoItem, _index) => {
      return _index !== index})
    setTodoList(filterArray)
  }

  const doneTodo = (index) => {
    todoList[index].isDone = true;
    console.log(todoList[index])
  }


  // if() {

  // } else if() {

  // }

  return (
    <div className="wrap">
      <div className="header">
        <span>My Todo List</span>
        <span>React</span>
      </div>

      <div className="under_header">
        <div className="under_header_1">
          제목
          <input type="text" ref={titleRef}></input>
          내용
          <input type="text" ref={contentRef}></input>
        </div>

        <div className="under_header_2">
          <button onClick={addContents}>추가하기</button>
        </div>
      </div>


      <div className='notdone'>
          {todoList.map((todoListName, index) => {
              return (
                <div className="box_wrap" key={index}>
                  <div className='todolistbox'>
                    <span>제목</span><span className='main'>{todoListName.title}</span>
                    <span>코멘트</span><span className='main'>{todoListName.content}</span>

                    <div className='mybtns'>
                      <button onClick={()=>{doneTodo(index)}}>완료</button>
                      <button onClick={()=>{deleteTodo(index)}}>삭제</button>
                    </div>
                  </div>
                </div>
              )
          })}
      </div>

      <div className='done'>

      </div>
      <div>

      </div>
    </div>
  );
}

export default App;
