import React from "react";

function List(props) {
  const { todoList, deleteTodo, togleTodo } = props;
  return (
    <div className="list_wrap">
      <div>
        <h1>WORKING</h1>
        <div className="whole">
          {todoList.map((todoListName, index) => {
            return (
              <div>
                {todoListName.isDone ? (
                  ""
                ) : (
                  <div className="box_wrap" key={index}>
                    <div className="todolistbox">
                      <span className="title">{todoListName.title}</span>
                      <span className="main">{todoListName.content}</span>

                      <div className="mybtns">
                        <button
                          onClick={() => {
                            deleteTodo(index);
                          }}
                        >
                          삭제
                        </button>
                        <button
                          onClick={() => {
                            togleTodo(index);
                          }}
                        >
                          완료
                        </button>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>

      <div>
        <h1>DONE</h1>

        {todoList.map((todoListName, index) => {
          return (
            <div>
              {todoListName.isDone ? (
                <div className="box_wrap" key={index}>
                  <div className="todolistbox">
                    <span className="title">{todoListName.title}</span>
                    <span className="main">{todoListName.content}</span>

                    <div className="mybtns">
                      <button
                        onClick={() => {
                          deleteTodo(index);
                        }}
                      >
                        삭제
                      </button>
                      <button
                        onClick={() => {
                          togleTodo(index);
                        }}
                      >
                        취소
                      </button>
                    </div>
                  </div>
                </div>
              ) : (
                ""
              )}
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default List;
