import './main.css';
import React, { useRef, useState } from 'react';
import Header from './components/header/Header';
import List from './components/list/List'

function App() {
  const [todoList, setTodoList] = useState([
    {
      title: "다이어트",
      content: "이번주 20kg 감량",
      isDone: false,
    },
    {
      title: "책읽기",
      content: "이번주 목표 3장 읽기",
      isDone: false,
    },
  ]);

  const titleRef = useRef();
  const contentRef = useRef();

  const addContents = () => {
    setTodoList([
      ...todoList,
      {
        title: titleRef.current.value,
        content: contentRef.current.value,
        isDone: false,
      },
    ]);
    titleRef.current.value = "";
    contentRef.current.value = "";
  };

  const deleteTodo = (index) => {
    const filterArray = todoList.filter((todoItem, _index) => {
      return _index !== index;
    });
    setTodoList(filterArray);
  };

  const togleTodo = (index) => {
    const doneList = todoList.map((todo, _index) => {
      if (index === _index) {
        return todo.isDone ? { ...todo, isDone: false } : { ...todo, isDone: true };
      } else return todo;
    });
    setTodoList(doneList);
  };

  return (
    <div className="wrap">
      <Header
        titleRef={titleRef}
        contentRef={contentRef}
        addContents={addContents}
      />
      <List
        todoList={todoList}
        deleteTodo={deleteTodo}
        togleTodo={togleTodo}
      />
    </div>
  );
}

export default App;
